document.addEventListener("DOMContentLoaded",()=>{const accordionItems=document.querySelectorAll(".sk-accordion-item");accordionItems.forEach(accordionItem=>{const accordionHeading=accordionItem.querySelector(".sk-accordion-heading");const accordionContent=accordionItem.querySelector(".sk-accordion-content");accordionHeading.addEventListener("click",()=>{accordionHeading.classList.toggle("active");if(accordionContent.style.maxHeight){accordionContent.style.maxHeight=null}else{accordionContent.style.maxHeight=`${accordionContent.scrollHeight}px`}})})});document.addEventListener("DOMContentLoaded",()=>{});